mapper.select:
SELECT a.message_content_id, a.user_id, a.role_id, a.content, a.status, a.create_date, a.update_date, b.message_type, b.icon, b.trade_id, c.login_name, d.amount, d.trade_no FROM t_message_content a LEFT JOIN t_message b ON a.trade_id=b.trade_id LEFT JOIN t_users c ON a.user_id=c.user_id LEFT JOIN t_user_account_detail d ON a.trade_id=d.trade_id WHERE a.role_id=#{roleId}
and a.user_id=#{userId}
and a.status = 0 ORDER BY a.create_date DESC limit 6

SELECT COUNT(1) FROM t_message_content WHERE role_id=#{roleId}
and user_id=#{userId}
and status = 0

