mapper.select:
SELECT t.task_id,t.user_id, t.task_title,t.task_content,t.task_type,t.task_status,t.hot, t.quantity,t.sale_price,t.sale_amount,t.lock_time, t.settle_coins,t.settle_time,t.create_date, t.phonenumber,t.nickname, a.answer_id,a.answer,a.odds, f.tfb_id,f.team_name,f.court_type, p.pic_id,p.pic_url,p.pic_type FROM ( SELECT tt.*, u.phonenumber,u.nickname FROM t_tasks tt JOIN m_users u on tt.user_id = u.user_id
JOIN ( SELECT gg.task_id,gg.user_id from t_task_orders gg where gg.is_delete = 0 GROUP BY gg.task_id,gg.user_id )as g on g.task_id = tt.task_id and g.user_id=#{userId}
tt.is_delete = 0
AND tt.task_content LIKE #{taskContent}
AND tt.task_status = #{task_status}
AND tt.create_date >= #{startTime}
AND tt.create_date <= #{endTime}
AND tt.user_id=#{userId}
ORDER BY tt.task_id DESC limit #{limit} offset #{offset} ) t LEFT JOIN t_task_answers a on a.task_id = t.task_id and a.is_delete = 0 LEFT JOIN t_task_football f on f.task_id = t.task_id and f.is_delete = 0 LEFT JOIN t_task_pictures p on p.task_id = t.task_id and p.is_delete = 0 ORDER BY t.task_id DESC,p.pic_type DESC

select count(1) cnt
FROM
t_tasks tt
JOIN m_users u on tt.user_id = u.user_id

    JOIN
    (
    SELECT gg.*
    from t_task_orders gg
    where  gg.is_delete = 0
    GROUP BY gg.task_id,gg.user_id
    )as g on g.task_id = tt.task_id  and g.user_id=#{userId}


    tt.is_delete = 0
    
        AND tt.task_content LIKE #{taskContent}
    
    
        AND tt.task_status = #{task_status}
    
    
         AND tt.create_date >= #{startTime} 
    
    
         AND tt.create_date <= #{endTime} 
    
    
        AND  tt.user_id=#{userId}

mapper.update:
UPDATE t_tasks
SET task_status = #{taskStatus},
    update_by = #{updateBy},
    update_date = #{updateDate}
WHERE task_id =#{taskId}

mapper.select:
select a.status from t_matches a
left join t_tasks b on a.match_id=b.match_id
where b.task_id=#{taskId}
